(()=>{'use strict'
    let deck=[];const tipos=['C','D','H','S'];const especiales=['A','J','Q','K'];let puntosJugador=0,puntosComputadora=0;const btnPedir=document.querySelector('#btnPedir');const btnDetener=document.querySelector('#btnDetener');const btnNuevo=document.querySelector('#btnNuevo');const divCartasJugador=document.querySelector('#jugador-cartas');const divCartasComputadora=document.querySelector('#computadora-cartas');const puntosHtml=document.querySelectorAll('small');const crearDeck=()=>{for(let i=2;i<=10;i++){for(let tipo of tipos){deck.push(i+tipo)}}
    for(let tipo of tipos){for(let esp of especiales){deck.push(esp+tipo)}}
    deck=_.shuffle(deck);console.log(deck);return deck}
    crearDeck();if(deck.length===0){throw'No hay cartas en el deck'}
    const pedirCarta=()=>{const carta=deck.pop();return carta}
    const valorCarta=(carta)=>{const valor=carta.substring(0,carta.length-1);return(isNaN(valor))?(valor==='A')?11:10:valor*1}
    const turnoComputadora=(puntoMinimos)=>{do{const carta=pedirCarta();puntosComputadora=puntosComputadora+valorCarta(carta);puntosHtml[1].innerText=puntosComputadora;const imgCarta=document.createElement('img');imgCarta.src=`assets/cartas/${carta}.png`;imgCarta.classList.add('carta');divCartasComputadora.append(imgCarta);if(puntoMinimos>21){break}}while((puntosComputadora<puntoMinimos)&&(puntoMinimos<=21));setTimeout(()=>{if(puntosComputadora===puntoMinimos){alert('Nadie gano :(')}else if(puntoMinimos>21){alert('Gano la computadora')}else if(puntosComputadora>21){alert('Jugador gano')}else{alert('Computadora gano')}},10)}
    btnPedir.addEventListener('click',()=>{const carta=pedirCarta();puntosJugador=puntosJugador+valorCarta(carta);puntosHtml[0].innerText=puntosJugador;const imgCarta=document.createElement('img');imgCarta.src=`assets/cartas/${carta}.png`;imgCarta.classList.add('carta');divCartasJugador.append(imgCarta);if(puntosJugador>21){console.warn('Perdiste');btnPedir.disabled=!0;btnDetener.disabled=!0;turnoComputadora(puntosJugador)}else if(puntosJugador===21){console.warn('21, genial');btnPedir.disabled=!0;btnDetener.disabled=!0;turnoComputadora(puntosJugador)}});btnDetener.addEventListener('click',()=>{btnPedir.disabled=!0;btnDetener.disabled=!0;turnoComputadora(puntosJugador)});btnNuevo.addEventListener('click',()=>{console.clear();deck=[];deck=crearDeck();puntosJugador=0;puntosComputadora=0;puntosHtml[0].innerText=0;puntosHtml[1].innerText=0;divCartasComputadora.innerHTML='';divCartasJugador.innerHTML='';btnPedir.disabled=!1;btnDetener.disabled=!1})})()



